import React, { useState } from 'react';

const InputElement = () => {

  const [inputText, setInputText] = useState("");

  return  <div>
            <input
              onChange={(e) => {
                setInputText(e.target.value)
              }}
              placeholder="Enter Some Text"/>;
          </div>
}

export default InputElement;
